import logo from "./logo.svg";
import { useState, useEffect, useRef } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [term, setTerm] = useState("javaScript");
  const [result, setResult] = useState([]);
  const termUseRef = useRef();
  const isInitialFetch = useRef(true);
  
  useEffect(() => {
    termUseRef.current = term;
  });
  const priviousTerm = termUseRef.current;
  useEffect(() => {
    console.log("useEffct");
    const Search = async () => {
      const response = await axios.get("https://en.wikipedia.org/w/api.php", {
        params: {
          action: "query",
          list: "search",
          srsearch: term,
          origin: "*",
          format: "json",
        },
      });
      setResult(response.data.query.search);
    };
    if (isInitialFetch.current) {
      if (term) {
        console.log(result.length,"result.length");
        Search();
      }
    } else if (priviousTerm !== term) {
      console.log("else if ");
      const timeOut = setTimeout(() => {
        console.log("set time out ");
        if (term) {
          Search();
        }
      }, 1200);
      return () => clearTimeout(timeOut);
    }
  }, [term, result.length, priviousTerm]);
  const fetchResult = result.map((el, index) => {
    return (
      <tr key={el.pageid}>
        <th scope="row">{index + 1}</th>
        <td>{el.title}</td>
        <td>{el.snippet}</td>
      </tr>
    );
  });
  return (
    <div className="App">
      <div className="container">
        <div className="row">
          <div className="col">
            <div className="my-3">
              <label htmlFor="exampleFormControleInput" className="form-label">
                Search input
              </label>
              <input
                type="text"
                className="form-control"
                id="exampleForControlInput"
                onChange={(e) => setTerm(e.target.value)}
                value={term}
              ></input>
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col">
            <table className="table">
              <thead>
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">Title</th>
                  <th scope="col">Description</th>
                </tr>
              </thead>
              <tbody>{fetchResult}</tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
